INSERT INTO "cf_names" VALUES('ocean_sigma_coordinate','1',NULL,NULL,'See Appendix D of the CF convention for information about parametric vertical coordinates. Note that the ocean sigma coordinate is not the same quantity as sea water sigma (excess of density over 1000 kg m-3), for which there are various other standard names.');
