INSERT INTO "cf_names" VALUES('mass_concentration_of_divinyl_chlorophyll_a_in_sea_water','kg m-3',NULL,NULL,'"Mass concentration" means mass per unit volume and is used in the construction "mass_concentration_of_X_in_Y", where X is a material constituent of Y. A chemical species or biological group denoted by X may be described by a single term such as "nitrogen" or a phrase such as "nox_expressed_as_nitrogen".');
