BEGIN TRANSACTION;
CREATE TABLE cf_aliases (
	id INTEGER NOT NULL, 
	alias VARCHAR NOT NULL, 
	standard_name VARCHAR NOT NULL, 
	PRIMARY KEY (id), 
	FOREIGN KEY(standard_name) REFERENCES cf_names (standard_name)
);
CREATE TABLE cf_names (
	standard_name VARCHAR NOT NULL, 
	canonical_units VARCHAR, 
	grib VARCHAR, 
	amip VARCHAR, 
	description TEXT, 
	PRIMARY KEY (standard_name)
);
CREATE TABLE "config" ("key" text,"value" text NOT NULL DEFAULT NULL, PRIMARY KEY (key));
CREATE TABLE ex_params (
	whp_name VARCHAR NOT NULL, 
	whp_number INTEGER, 
	description TEXT, 
	note TEXT, 
	warning TEXT, 
	scope VARCHAR(7) DEFAULT 'sample' NOT NULL, 
	dtype VARCHAR(7) NOT NULL, 
	flag VARCHAR(13) NOT NULL, 
	ancillary BOOLEAN DEFAULT '0' NOT NULL, 
	rank FLOAT NOT NULL, 
	PRIMARY KEY (whp_name), 
	CHECK (scope IN ('cruise', 'profile', 'sample')), 
	CHECK (dtype IN ('decimal', 'integer', 'string')), 
	CHECK (flag IN ('woce_bottle', 'woce_ctd', 'woce_discrete', 'no_flags')), 
	CHECK (ancillary IN (0, 1))
);

