CREATE TABLE ex_units (
	id INTEGER NOT NULL, 
	whp_unit VARCHAR, 
	cf_unit VARCHAR NOT NULL, 
	reference_scale VARCHAR, 
	note TEXT, 
	PRIMARY KEY (id), 
	UNIQUE (whp_unit)
);
CREATE TABLE whp_alias (
	old_name VARCHAR NOT NULL, 
	old_unit VARCHAR, 
	whp_name VARCHAR, 
	whp_unit VARCHAR, 
	PRIMARY KEY (old_name, old_unit), 
	FOREIGN KEY(whp_name, whp_unit) REFERENCES whp_names (whp_name, whp_unit)
);
CREATE TABLE whp_names (
	whp_name VARCHAR NOT NULL, 
	whp_unit VARCHAR, 
	standard_name VARCHAR, 
	nc_name VARCHAR, 
	numeric_min FLOAT, 
	numeric_max FLOAT, 
	error_name VARCHAR, 
	analytical_temperature_name VARCHAR, 
	analytical_temperature_units VARCHAR, 
	field_width INTEGER NOT NULL, 
	numeric_precision INTEGER, "radiation_wavelength" FLOAT, "scattering_angle" FLOAT, "excitation_wavelength" FLOAT, "emission_wavelength" FLOAT, 
	PRIMARY KEY (whp_name, whp_unit), 
	FOREIGN KEY(analytical_temperature_name, analytical_temperature_units) REFERENCES whp_names (whp_name, whp_unit), 
	FOREIGN KEY(whp_name) REFERENCES ex_params (whp_name), 
	FOREIGN KEY(whp_unit) REFERENCES ex_units (whp_unit), 
	FOREIGN KEY(standard_name) REFERENCES cf_names (standard_name), 
	UNIQUE (nc_name)
);
COMMIT;
